export async function callForUser(accessToken){

    const headers = new Headers();
    const bearer = `bearer ${accessToken}`;
    headers.append("Authorization", bearer);

    const options = {
        method: "GET",
        headers: headers
    };
    const endpoint = "https://graph.microsoft.com/v1.0/users?$top=999";

    return fetch(endpoint, options)
        .then(response => response.json())
        .catch(error => console.log(error))
}

export async function callForEvent(accessToken, user){

    const headers = new Headers();
    const bearer = `bearer ${accessToken}`;
    headers.append("Authorization", bearer);

    const options = {
        method: "GET",
        headers: headers
    };

    const endpoint = `https://graph.microsoft.com/v1.0/users/${user}/events?$top=1`;

    if(user.length > 12){
        return ('The user is out of Scope')
    } else {
        return fetch(endpoint, options)
        .then(response => response.json())
        .catch(error => console.log(error))
    }


}
