import React from "react";
import { useMsal } from "@azure/msal-react";
import { useState } from "react"
import { loginRequest } from '../../AuthenticationFolder/authConfig'
import Button from "react-bootstrap/Button"
import { callForUser, callForNextUser } from "./GraphCalls"

function handleLogin(instance) {
    //Handles login and catches exceptions
    instance.loginRedirect(loginRequest).catch(e => {
        console.error(e);
    });
}

function handleLogout(instance){
    //Handles login and catches exceptions
    instance.logoutRedirect().catch(e => {
        console.error(e);
    });
}

export function GetUsers(){
    const { instance, accounts } = useMsal();
    const [userList, setUserList] = useState(null);

    var userArr = {};

    const name = accounts[0] && accounts[0].name;

    function RequestUsers(){
        const request = {
            ...loginRequest,
            account: accounts[0]
        };

        instance.acquireTokenSilent(request).then((response) => {
            callForUser(response.accessToken).then(response => setUserList(response));
            console.log(userList);
        }).catch((e) => {
            instance.acquireTokenPopup(request).then(response => {
                callForUser(response.accessToken).then(response => setUserList(response))
            })
        })
        userList.value.forEach(element => {
            userArr.append(element)
        });
        console.log(userArr)

/*
        if("@odata.nextLink" in userList){
            console.log('hej');
            instance.acquireTokenSilent(request).then((response) => {
                callForNextUser(response.accessToken, userList['@odata.nextLink']).then(response => setUserList(response));
                console.log(userList)
            }) 
        }*/
    }
    return (
        <>
        <h2>{name} is logged in!</h2>
        <button onClick={() => RequestUsers()}>Click me to retrieve data</button>
        <button onClick={() => handleLogout(instance)}>Click me to logout</button>
        </>
    )
}



export const ReqUsers = () => {
    //Bruger Msal (Microsfot Authentication Library.)
    const { instance } = useMsal();
    return (
        //Button class imported from react-boostrap
        <Button className="ml-auto" onClick={() => handleLogin(instance)}>Sign in using Redirect</Button>
    );
}
